    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        View view = inflater.inflate(R.layout.activity_music, container,false);
        /**
         * 初始化控件
         */
        thisView=view;
        InitUI();
        long musticId=GetMusticId();
        InitData(musticId);
//        Toast.makeText(getContext(), "刷新成功！正在加载，请等待。", Toast.LENGTH_LONG).show();
        startTime=System.currentTimeMillis();
        MyMusticList.add(musticId);
        IndexOfMustic+=1;
        return view;
    }
    //初始化数据
    private void InitData(long musticId) {
        Urlpath="https://v1.alapi.cn/api/music/detail?id="+musticId;
        new Thread(new Runnable() {
            @Override
            public void run() {
                GetDataFromUrl(Urlpath,GET_DATA_FLAG);
            }
        }).start();


    }

    //初始化控件
    private void InitUI() {
        imageView = thisView.findViewById(R.id.image);
        singInfor = thisView.findViewById(R.id.singInfor);
        start_stop = thisView.findViewById(R.id.play);//播放——暂停
        hotcoment = thisView.findViewById(R.id.hotcoment);
        hotcomentuser =thisView.findViewById(R.id.hotcomentuser);
        mustic_prev = thisView.findViewById(R.id.mustic_prev);//上一首
        mustic_next = thisView.findViewById(R.id.mustic_next);//下一首
        start_stop.setOnClickListener(this);
        mustic_prev.setOnClickListener(this);
        mustic_next.setOnClickListener(this);
        hotcoment.setMovementMethod(ScrollingMovementMethod.getInstance());
    }

    //按钮事件
    @Override
    public void onClick(View v) {
        switch (v.getId()){
            case R.id.play:
                MusticStart_Or_Stop();
                break;
            case R.id.mustic_prev:
                endTime= System.currentTimeMillis();
                if(MyMusticList.size()==1 || IndexOfMustic==0){
                    //只有一首音乐or当前为第一首音乐
                    Toast.makeText(getContext(), "已无上一首音乐", Toast.LENGTH_SHORT).show();
                }else if((endTime-startTime)<=5000){
                    Toast.makeText(getContext(), "刷新太频繁，请稍后重试！", Toast.LENGTH_SHORT).show();
                } else{
                    //位置前移，加载上一首音乐
                    IndexOfMustic-=1;
                    Toast.makeText(getContext(), "正在加载，请等待。", Toast.LENGTH_SHORT).show();
                    InitData((long)MyMusticList.get(IndexOfMustic));
                    //暂停并清除当前音乐
                    StopAndClear();
                }
                break;
            case R.id.mustic_next:
                endTime= System.currentTimeMillis();
                if(IndexOfMustic==(MyMusticList.size()-1) && ((endTime-startTime)>5000)){
                    //当前音乐为列表中的最后一首 5秒等待
                    long musticId=GetMusticId();
                    InitData(musticId);
                    MyMusticList.add(musticId);
                    IndexOfMustic+=1;
                    //暂停并清除当前音乐
                    StopAndClear();
                    Toast.makeText(getContext(), "正在加载，请等待。", Toast.LENGTH_LONG).show();

                }else if((endTime-startTime)>5000){
                    //位置后移，加载下一首音乐 5秒
                    IndexOfMustic+=1;
                    InitData((long)MyMusticList.get(IndexOfMustic));
                    //暂停并清除当前音乐
                    StopAndClear();
                    Toast.makeText(getContext(), "正在加载，请等待。", Toast.LENGTH_LONG).show();
                }else{
                    Toast.makeText(getContext(), "刷新太频繁，请稍后重试！", Toast.LENGTH_SHORT).show();
                }
                break;
        }

    }

    //暂停并清除当前音乐
    private void StopAndClear() {
        mediaPlayer.stop();
        Start_Or_Stop=0;
        start_stop.setBackgroundResource(R.mipmap.ic_play);
        mediaPlayer.release();
        Start_Or_Stop=0;
        startTime=endTime;
    }

    //自动播放
    private void AutoPlayMustic(){
        mediaPlayer.start();
        Start_Or_Stop=1;
        start_stop.setBackgroundResource(R.mipmap.ic_stop);
    }



    //从网络中获取数据
    private String GetDataFromUrl(String urlpath, int FLAG) {
        try{
            //1.创建URL
            URL url = new URL(urlpath);
            //2.打开链接
            connection = (HttpURLConnection)url.openConnection();
            //3.判断并处理结果
            if(connection.getResponseCode()==200){
                //获取输入流
                inputStream = connection.getInputStream();
                switch (FLAG) {
                    case 101:
                        //文本数据
                        bufferedReader = new BufferedReader(new InputStreamReader(inputStream));
                        StringBuilder stringBuilder = new StringBuilder();
                        for (String line = ""; (line = bufferedReader.readLine()) != null; ) {
                            stringBuilder.append(line);
                        }
                        String data = stringBuilder.toString();
                        Message message = Message.obtain();
                        Bundle bundle = new Bundle();
                        bundle.putString("data", data);
                        message.setData(bundle);
                        message.what = GET_DATA_SUCCESS;
                        //向主线程发送数据
                        handler.sendMessage(message);
                        break;

                    case 102:
                        //图片数据
                        //使用工厂把网络的输入流生产Bitmap
                        Bitmap bitmap = BitmapFactory.decodeStream(inputStream);
                        //利用Message把图片发给Handler
                        Message msg = Message.obtain();
                        msg.obj = bitmap;
                        msg.what = GET_IMAGE_SUCCESS;
                        handler.sendMessage(msg);
                        break;
                }
            }
        }catch (Exception e){
            e.printStackTrace();
        }finally {
            try{
                if(bufferedReader!=null){
                    bufferedReader.close();
                }
                if(inputStream!=null){
                    inputStream.close();
                }
                if(connection!=null){
                    connection.disconnect();
                }

            }catch (Exception e){
                e.printStackTrace();
            }
        }
        return "";

    }

    //线程通信
    Handler handler=new Handler(new Handler.Callback() {
        @Override
        public boolean handleMessage(@NonNull Message msg) {
            if(msg.what==GET_DATA_SUCCESS){
                //文本数据
                String data=msg.getData().getString("data");
//                Log.i("原数据","   :"+data);
                parseJSONWithJSONObject(data,1);
            }
            if(msg.what==GET_IMAGE_SUCCESS){
                //图片数据
                Bitmap bitmap = (Bitmap) msg.obj;
                imageView.setImageBitmap(bitmap);
            }
            return false;
        }
    });

    //提取文本数据
    private void parseJSONWithJSONObject(String data,int num) {
        try{

            switch (num){
                case 1:
                    JSONObject jsonObject=new JSONObject(data);
                    String data1=jsonObject.getString("data");
//                    Log.i("内容1","   :"+data1);
                    parseJSONWithJSONObject(data1,2);
                    break;
                case 2:
                    JSONObject jsonObject2=new JSONObject(data);
                    String data2=jsonObject2.getString("songs");
//                    Log.i("内容2","   :"+data2);
                    parseJSONWithJSONObject(data2,3);
                    break;
                case 3:
                    JSONArray jsonArray=new JSONArray(data);
                    for(int i=0;i<jsonArray.length();i++) {
                        JSONObject jsonObject3 = jsonArray.getJSONObject(i);
                        String song_id = jsonObject3.getString("id");
                        String song_name = jsonObject3.getString("name");
                        String singer = getSinger(jsonObject3.getString("ar"));
                        //Log.i("内容3", "   :" + jsonObject3.getString("ar"));
                        String pic_url = getPicUrl(jsonObject3.getString("al"));
                        // Log.i("内容3", "   :" + jsonObject3.getString("al"));

                        String song_url = "https://v1.alapi.cn/api/music/url?id=" + song_id;

                        NextNeedDo(song_url, song_name, singer, pic_url);
                    }
                    break;

            }

        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    //获取歌手名称
    private String getSinger(String ar) throws JSONException {
        JSONArray jsonArray= new JSONArray(ar);
        String name="";
        for(int i=0;i<jsonArray.length();i++) {
            JSONObject jsonObject = jsonArray.getJSONObject(i);
            name = jsonObject.getString("name");
        }
        return name;
    }


